import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { 
  CheckCircleIcon, 
  ClockIcon, 
  ExclamationTriangleIcon,
  PlusIcon,
  ArrowTrendingUpIcon,
  ChartBarIcon,
  UserGroupIcon,
  CalendarIcon,
  ChevronRightIcon
} from '@heroicons/react/24/outline';
import { useApp } from '../context/AppContext';

const Dashboard = () => {
  const { tasks, setActivePage } = useApp();
  const [filter, setFilter] = useState('all');

  // Add safety check for tasks
  const safeTasks = tasks || [];

  // Calculate statistics
  const totalTasks = safeTasks.length;
  const completedTasks = safeTasks.filter(task => task.isDone).length;
  const pendingTasks = totalTasks - completedTasks;
  const highPriorityTasks = safeTasks.filter(task => task.priority === 'High' && !task.isDone).length;
  const completionRate = totalTasks > 0 ? Math.round((completedTasks / totalTasks) * 100) : 0;

  // Get overdue tasks (simplified - checking if due date is past)
  const today = new Date();
  const overdueTasks = safeTasks.filter(task => {
    if (!task.dueDate || task.isDone) return false;
    return new Date(task.dueDate) < today;
  }).length;

  // Filter tasks based on selected filter
  const filteredTasks = safeTasks.filter(task => {
    if (filter === 'all') return true;
    if (filter === 'completed') return task.isDone;
    if (filter === 'pending') return !task.isDone;
    if (filter === 'high') return task.priority === 'High';
    return true;
  });

  // Get recent tasks (last 5)
  const recentTasks = filteredTasks
    .sort((a, b) => new Date(b.createdAt || Date.now()) - new Date(a.createdAt || Date.now()))
    .slice(0, 5);

  const stats = [
    {
      title: 'Total Tasks',
      value: totalTasks,
      icon: ChartBarIcon,
      color: 'from-blue-500 to-indigo-600',
      trend: '+12%'
    },
    {
      title: 'Completed',
      value: completedTasks,
      icon: CheckCircleIcon,
      color: 'from-green-500 to-emerald-600',
      trend: `${completionRate}%`
    },
    {
      title: 'Pending',
      value: pendingTasks,
      icon: ClockIcon,
      color: 'from-amber-500 to-yellow-600',
      trend: '-5%'
    },
    {
      title: 'High Priority',
      value: highPriorityTasks,
      icon: ExclamationTriangleIcon,
      color: 'from-rose-500 to-red-600',
      trend: `${highPriorityTasks} urgent`
    }
  ];

  const quickActions = [
    {
      title: 'Add New Task',
      description: 'Create a new task quickly',
      icon: PlusIcon,
      action: () => setActivePage('tasks'),
      color: 'bg-gradient-to-r from-blue-500 to-indigo-600'
    },
    {
      title: 'View All Tasks',
      description: 'Manage your task list',
      icon: ChartBarIcon,
      action: () => setActivePage('tasks'),
      color: 'bg-gradient-to-r from-emerald-500 to-teal-600'
    },
    {
      title: 'Team Members',
      description: 'View your team',
      icon: UserGroupIcon,
      action: () => setActivePage('team'),
      color: 'bg-gradient-to-r from-purple-500 to-fuchsia-600'
    },
    {
      title: 'Analytics',
      description: 'View performance metrics',
      icon: ArrowTrendingUpIcon,
      action: () => setActivePage('analytics'),
      color: 'bg-gradient-to-r from-amber-500 to-orange-600'
    }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.3
      }
    }
  };

  const getPriorityClass = (priority) => {
    switch (priority.toLowerCase()) {
      case 'high': return 'bg-red-100 text-red-800';
      case 'medium': return 'bg-yellow-100 text-yellow-800';
      case 'low': return 'bg-blue-100 text-blue-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <motion.div 
      className="w-full max-w-7xl mx-auto px-4 py-6 sm:px-6"
      variants={containerVariants}
      initial="hidden"
      animate="visible"
    >
      {/* Welcome Section */}
      <motion.div 
        className="flex flex-col md:flex-row justify-between items-start md:items-center mb-8"
        variants={itemVariants}
      >
        <div>
          <h1 className="text-2xl md:text-3xl font-bold text-gray-800">Welcome back! 👋</h1>
          <p className="text-gray-600 mt-2">Here's what's happening with your projects today.</p>
        </div>
        <motion.button 
          className="mt-4 md:mt-0 flex items-center px-4 py-2 bg-gradient-to-r from-blue-600 to-indigo-700 text-white rounded-lg font-medium shadow-md hover:shadow-lg transition-all"
          onClick={() => setActivePage('tasks')}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          <PlusIcon className="w-5 h-5 mr-2" />
          Add New Task
        </motion.button>
      </motion.div>

      {/* Stats Grid */}
      <motion.div 
        className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8"
        variants={itemVariants}
      >
        {stats.map((stat, index) => {
          const Icon = stat.icon;
          return (
            <motion.div
              key={stat.title}
              className={`bg-white rounded-xl shadow p-5 border border-gray-100`}
              variants={itemVariants}
              whileHover={{ y: -5, boxShadow: '0 10px 25px -5px rgba(0, 0, 0, 0.1)' }}
            >
              <div className="flex justify-between items-start">
                <div>
                  <p className="text-gray-500 text-sm font-medium">{stat.title}</p>
                  <h3 className="text-2xl font-bold text-gray-800 mt-1">{stat.value}</h3>
                  <p className="text-sm mt-2 font-medium text-blue-600">{stat.trend}</p>
                </div>
                <div className={`p-3 rounded-lg bg-gradient-to-r ${stat.color}`}>
                  <Icon className="w-6 h-6 text-white" />
                </div>
              </div>
            </motion.div>
          );
        })}
      </motion.div>

      {/* Main Content Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Quick Actions */}
        <motion.div 
          className="lg:col-span-1 bg-white rounded-xl shadow p-5 border border-gray-100"
          variants={itemVariants}
        >
          <div className="flex justify-between items-center mb-5">
            <h2 className="text-xl font-bold text-gray-800">Quick Actions</h2>
          </div>
          <div className="space-y-4">
            {quickActions.map((action, index) => {
              const Icon = action.icon;
              return (
                <motion.button
                  key={action.title}
                  className={`w-full flex items-center p-4 rounded-xl text-left transition-all ${action.color} text-white`}
                  onClick={action.action}
                  variants={itemVariants}
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                >
                  <div className="mr-4">
                    <Icon className="w-6 h-6" />
                  </div>
                  <div>
                    <h3 className="font-semibold">{action.title}</h3>
                    <p className="text-sm opacity-90">{action.description}</p>
                  </div>
                </motion.button>
              );
            })}
          </div>
        </motion.div>

        {/* Recent Tasks */}
        <motion.div 
          className="lg:col-span-2 bg-white rounded-xl shadow p-5 border border-gray-100"
          variants={itemVariants}
        >
          <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-5">
            <h2 className="text-xl font-bold text-gray-800 mb-3 sm:mb-0">Recent Tasks</h2>
            <div className="flex space-x-2">
              <button 
                className={`px-3 py-1 text-sm rounded-lg ${filter === 'all' ? 'bg-blue-100 text-blue-700' : 'bg-gray-100 text-gray-700'}`}
                onClick={() => setFilter('all')}
              >
                All
              </button>
              <button 
                className={`px-3 py-1 text-sm rounded-lg ${filter === 'pending' ? 'bg-amber-100 text-amber-700' : 'bg-gray-100 text-gray-700'}`}
                onClick={() => setFilter('pending')}
              >
                Pending
              </button>
              <button 
                className={`px-3 py-1 text-sm rounded-lg ${filter === 'completed' ? 'bg-green-100 text-green-700' : 'bg-gray-100 text-gray-700'}`}
                onClick={() => setFilter('completed')}
              >
                Completed
              </button>
              <button 
                className={`px-3 py-1 text-sm rounded-lg ${filter === 'high' ? 'bg-red-100 text-red-700' : 'bg-gray-100 text-gray-700'}`}
                onClick={() => setFilter('high')}
              >
                High Priority
              </button>
            </div>
          </div>
          
          <div className="space-y-3">
            {recentTasks.length > 0 ? (
              recentTasks.map((task) => (
                <motion.div
                  key={task._id}
                  className="flex items-center justify-between p-4 border border-gray-200 rounded-lg hover:bg-gray-50 transition-colors"
                  variants={itemVariants}
                  whileHover={{ x: 4 }}
                >
                  <div className="flex items-center">
                    <div className={`w-3 h-3 rounded-full mr-3 ${task.isDone ? 'bg-green-500' : 'bg-amber-500'}`} />
                    <div>
                      <h4 className={`font-medium ${task.isDone ? 'line-through text-gray-500' : 'text-gray-800'}`}>
                        {task.taskName}
                      </h4>
                      <div className="flex flex-wrap gap-2 mt-1">
                        <span className={`text-xs px-2 py-1 rounded-full ${getPriorityClass(task.priority)}`}>
                          {task.priority} priority
                        </span>
                        {task.dueDate && (
                          <span className="text-xs px-2 py-1 rounded-full bg-gray-100 text-gray-700 flex items-center">
                            <CalendarIcon className="w-3 h-3 mr-1" />
                            Due: {new Date(task.dueDate).toLocaleDateString()}
                          </span>
                        )}
                      </div>
                    </div>
                  </div>
                  <button 
                    className="text-gray-500 hover:text-blue-600"
                    onClick={() => setActivePage('tasks')}
                  >
                    <ChevronRightIcon className="w-5 h-5" />
                  </button>
                </motion.div>
              ))
            ) : (
              <motion.div 
                className="text-center py-10"
                variants={itemVariants}
              >
                <div className="w-16 h-16 mx-auto bg-gray-100 rounded-full flex items-center justify-center mb-4">
                  <ClipboardDocumentListIcon className="w-8 h-8 text-gray-400" />
                </div>
                <h3 className="font-medium text-gray-700">No tasks found</h3>
                <p className="text-gray-500 mt-1 mb-4">Start by creating your first task</p>
                <button 
                  className="px-4 py-2 bg-gradient-to-r from-blue-600 to-indigo-700 text-white rounded-lg font-medium shadow hover:shadow-md transition-all"
                  onClick={() => setActivePage('tasks')}
                >
                  Create Task
                </button>
              </motion.div>
            )}
          </div>
          
          {recentTasks.length > 0 && (
            <div className="mt-5 text-center">
              <button 
                className="px-4 py-2 bg-gray-100 text-gray-700 rounded-lg font-medium hover:bg-gray-200 transition-colors"
                onClick={() => setActivePage('tasks')}
              >
                View All Tasks
              </button>
            </div>
          )}
        </motion.div>
      </div>

      {/* Alerts */}
      {overdueTasks > 0 && (
        <motion.div 
          className="mt-8 flex items-center justify-between bg-amber-50 border border-amber-200 rounded-xl p-4"
          variants={itemVariants}
          initial={{ scale: 0.9, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
        >
          <div className="flex items-center">
            <ExclamationTriangleIcon className="w-6 h-6 text-amber-600 mr-3" />
            <div>
              <h3 className="font-medium text-amber-800">Attention needed</h3>
              <p className="text-amber-700 text-sm">
                You have {overdueTasks} overdue task{overdueTasks > 1 ? 's' : ''} that require your attention
              </p>
            </div>
          </div>
          <button 
            className="px-4 py-2 bg-amber-100 text-amber-800 rounded-lg font-medium hover:bg-amber-200 transition-colors"
            onClick={() => setActivePage('tasks')}
          >
            View Tasks
          </button>
        </motion.div>
      )}
    </motion.div>
  );
};

export default Dashboard;
