import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  HomeIcon, 
  ClipboardDocumentListIcon, 
  InformationCircleIcon,
  UserIcon,
  UserPlusIcon,
  ArrowRightOnRectangleIcon,
  Bars3Icon,
  XMarkIcon,
  ChevronDownIcon,
  ChartBarIcon,
  Cog6ToothIcon
} from '@heroicons/react/24/outline';

const Header = () => {
  const [activePage, setActivePage] = useState('dashboard');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState({ name: 'Alex Morgan' });
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isGetStartedOpen, setIsGetStartedOpen] = useState(false);
  const [isProfileOpen, setIsProfileOpen] = useState(false);
  const dropdownRef = useRef(null);
  const profileRef = useRef(null);

  const logout = () => {
    setIsAuthenticated(false);
    setActivePage('dashboard');
  };

  const login = () => {
    setIsAuthenticated(true);
    setActivePage('dashboard');
  };

  // Close dropdowns when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsGetStartedOpen(false);
      }
      if (profileRef.current && !profileRef.current.contains(event.target)) {
        setIsProfileOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const navigationItems = [
    { id: 'dashboard', label: 'Home', icon: HomeIcon },
    { id: 'projects', label: 'Projects', icon: ClipboardDocumentListIcon },
    { id: 'analytics', label: 'Analytics', icon: ChartBarIcon },
    { id: 'about', label: 'About', icon: InformationCircleIcon },
  ];

  const authItems = isAuthenticated 
    ? [
        { id: 'profile', label: user?.name || 'Profile', icon: UserIcon, action: () => setIsProfileOpen(!isProfileOpen) },
        { id: 'logout', label: 'Logout', icon: ArrowRightOnRectangleIcon, action: logout }
      ]
    : [
        { id: 'get-started', label: 'Get Started', icon: UserPlusIcon, action: () => setIsGetStartedOpen(!isGetStartedOpen) }
      ];

  const handleNavClick = (item) => {
    if (item.action) {
      item.action();
    } else {
      setActivePage(item.id);
    }
    setIsMobileMenuOpen(false);
  };

  return (
    <motion.header 
      className="fixed top-0 left-0 right-0 z-50 bg-white border-b border-gray-100 shadow-sm"
      initial={{ y: -100, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16 items-center">
          {/* Logo */}
          <motion.div 
            className="flex items-center cursor-pointer"
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
            onClick={() => setActivePage('dashboard')}
          >
            <div className="bg-gradient-to-r from-blue-600 to-indigo-700 w-8 h-8 rounded-lg flex items-center justify-center mr-2">
              <ClipboardDocumentListIcon className="h-5 w-5 text-white" />
            </div>
            <div className="text-xl font-bold">
              <span className="text-gray-800">Project</span>
              <span className="text-blue-600">Flow</span>
            </div>
          </motion.div>

          {/* Desktop Navigation */}
          <div className="hidden md:flex items-center space-x-1">
            {navigationItems.map((item) => {
              const Icon = item.icon;
              return (
                <motion.button
                  key={item.id}
                  className={`flex items-center px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                    activePage === item.id 
                      ? 'text-blue-600 bg-blue-50' 
                      : 'text-gray-600 hover:bg-gray-50'
                  }`}
                  onClick={() => handleNavClick(item)}
                  whileHover={{ y: -2 }}
                  whileTap={{ scale: 0.98 }}
                >
                  <Icon className="h-5 w-5 mr-2" />
                  {item.label}
                </motion.button>
              );
            })}
          </div>

          {/* Desktop Auth Section */}
          <div className="hidden md:flex items-center space-x-2">
            {authItems.map((item) => {
              const Icon = item.icon;
              
              if (item.id === 'get-started') {
                return (
                  <div key={item.id} className="relative" ref={dropdownRef}>
                    <motion.button
                      className="flex items-center px-4 py-2 rounded-lg text-sm font-medium text-white bg-gradient-to-r from-blue-600 to-indigo-700 hover:from-blue-700 hover:to-indigo-800 transition-all"
                      onClick={item.action}
                      whileHover={{ y: -2 }}
                      whileTap={{ scale: 0.96 }}
                    >
                      <Icon className="h-4 w-4 mr-2" />
                      <span>{item.label}</span>
                      <ChevronDownIcon className={`h-4 w-4 ml-1 transition-transform ${isGetStartedOpen ? 'rotate-180' : ''}`} />
                    </motion.button>
                    
                    <AnimatePresence>
                      {isGetStartedOpen && (
                        <motion.div
                          className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg py-1 border border-gray-100 z-10"
                          initial={{ opacity: 0, y: -10 }}
                          animate={{ opacity: 1, y: 0 }}
                          exit={{ opacity: 0, y: -10 }}
                          transition={{ duration: 0.2 }}
                        >
                          <motion.button
                            className="flex items-center w-full px-4 py-2 text-sm text-gray-700 hover:bg-gray-50"
                            onClick={() => {
                              setActivePage('login');
                              setIsGetStartedOpen(false);
                            }}
                            whileHover={{ x: 4 }}
                          >
                            <UserIcon className="h-4 w-4 mr-3" />
                            <span>Login</span>
                          </motion.button>
                          <motion.button
                            className="flex items-center w-full px-4 py-2 text-sm text-gray-700 hover:bg-gray-50"
                            onClick={() => {
                              setActivePage('signup');
                              setIsGetStartedOpen(false);
                            }}
                            whileHover={{ x: 4 }}
                          >
                            <UserPlusIcon className="h-4 w-4 mr-3" />
                            <span>Sign Up</span>
                          </motion.button>
                        </motion.div>
                      )}
                    </AnimatePresence>
                  </div>
                );
              }
              
              if (item.id === 'profile') {
                return (
                  <div key={item.id} className="relative" ref={profileRef}>
                    <motion.button
                      className="flex items-center px-3 py-2 rounded-lg text-sm font-medium text-gray-700 hover:bg-gray-50 transition-colors"
                      onClick={item.action}
                      whileHover={{ y: -2 }}
                      whileTap={{ scale: 0.96 }}
                    >
                      <div className="w-8 h-8 rounded-full bg-gradient-to-r from-blue-400 to-indigo-500 flex items-center justify-center text-white font-medium mr-2">
                        {user?.name?.charAt(0) || 'U'}
                      </div>
                      <span className="hidden lg:inline">{user?.name || 'Profile'}</span>
                      <ChevronDownIcon className={`h-4 w-4 ml-1 transition-transform ${isProfileOpen ? 'rotate-180' : ''}`} />
                    </motion.button>
                    
                    <AnimatePresence>
                      {isProfileOpen && (
                        <motion.div
                          className="absolute right-0 mt-2 w-56 bg-white rounded-lg shadow-lg py-2 border border-gray-100 z-10"
                          initial={{ opacity: 0, y: -10 }}
                          animate={{ opacity: 1, y: 0 }}
                          exit={{ opacity: 0, y: -10 }}
                          transition={{ duration: 0.2 }}
                        >
                          <div className="px-4 py-3 border-b border-gray-100">
                            <div className="font-medium text-gray-900">{user?.name || 'User'}</div>
                            <div className="text-sm text-gray-500 truncate">user@example.com</div>
                          </div>
                          <div className="py-1">
                            <button className="flex items-center w-full px-4 py-2 text-sm text-gray-700 hover:bg-gray-50">
                              <UserIcon className="h-4 w-4 mr-3 text-gray-500" />
                              <span>Your Profile</span>
                            </button>
                            <button className="flex items-center w-full px-4 py-2 text-sm text-gray-700 hover:bg-gray-50">
                              <Cog6ToothIcon className="h-4 w-4 mr-3 text-gray-500" />
                              <span>Settings</span>
                            </button>
                          </div>
                          <div className="py-1 border-t border-gray-100">
                            <button 
                              className="flex items-center w-full px-4 py-2 text-sm text-gray-700 hover:bg-gray-50"
                              onClick={logout}
                            >
                              <ArrowRightOnRectangleIcon className="h-4 w-4 mr-3 text-gray-500" />
                              <span>Sign out</span>
                            </button>
                          </div>
                        </motion.div>
                      )}
                    </AnimatePresence>
                  </div>
                );
              }
              
              return (
                <motion.button
                  key={item.id}
                  className={`flex items-center px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                    item.id === 'logout' 
                      ? 'text-gray-700 hover:text-red-600 hover:bg-red-50' 
                      : 'text-gray-700 hover:bg-gray-50'
                  }`}
                  onClick={item.action}
                  whileHover={{ y: -2 }}
                  whileTap={{ scale: 0.96 }}
                >
                  <Icon className="h-4 w-4 mr-2" />
                  <span>{item.label}</span>
                </motion.button>
              );
            })}
          </div>

          {/* Mobile Menu Button */}
          <div className="md:hidden">
            <motion.button
              className="p-2 rounded-lg text-gray-600 hover:bg-gray-100"
              onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
              whileTap={{ scale: 0.95 }}
            >
              {isMobileMenuOpen ? (
                <XMarkIcon className="h-6 w-6" />
              ) : (
                <Bars3Icon className="h-6 w-6" />
              )}
            </motion.button>
          </div>
        </div>
      </div>

      {/* Mobile Menu */}
      <AnimatePresence>
        {isMobileMenuOpen && (
          <motion.div
            className="md:hidden bg-white"
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: 'auto' }}
            exit={{ opacity: 0, height: 0 }}
            transition={{ duration: 0.3 }}
          >
            <div className="px-2 pt-2 pb-4 space-y-1 sm:px-3 border-t border-gray-100">
              {navigationItems.map((item) => {
                const Icon = item.icon;
                return (
                  <motion.button
                    key={item.id}
                    className={`flex items-center w-full px-3 py-3 rounded-lg text-base font-medium ${
                      activePage === item.id 
                        ? 'text-blue-600 bg-blue-50' 
                        : 'text-gray-700 hover:bg-gray-50'
                    }`}
                    onClick={() => handleNavClick(item)}
                    whileHover={{ x: 4 }}
                    whileTap={{ scale: 0.98 }}
                  >
                    <Icon className="h-5 w-5 mr-3" />
                    {item.label}
                  </motion.button>
                );
              })}
              
              <div className="pt-2 border-t border-gray-100">
                {isAuthenticated ? (
                  authItems.map((item) => {
                    const Icon = item.icon;
                    return (
                      <motion.button
                        key={item.id}
                        className={`flex items-center w-full px-3 py-3 rounded-lg text-base font-medium ${
                          item.id === 'logout' 
                            ? 'text-gray-700 hover:text-red-600 hover:bg-red-50' 
                            : 'text-gray-700 hover:bg-gray-50'
                        }`}
                        onClick={() => handleNavClick(item)}
                        whileHover={{ x: 4 }}
                        whileTap={{ scale: 0.98 }}
                      >
                        <Icon className="h-5 w-5 mr-3" />
                        {item.label}
                      </motion.button>
                    );
                  })
                ) : (
                  <>
                    <motion.button
                      className="flex items-center w-full px-3 py-3 rounded-lg text-base font-medium text-gray-700 hover:bg-gray-50"
                      onClick={() => {
                        setActivePage('login');
                        setIsMobileMenuOpen(false);
                      }}
                      whileHover={{ x: 4 }}
                      whileTap={{ scale: 0.98 }}
                    >
                      <UserIcon className="h-5 w-5 mr-3" />
                      <span>Login</span>
                    </motion.button>
                    <motion.button
                      className="flex items-center w-full px-3 py-3 rounded-lg text-base font-medium text-white bg-gradient-to-r from-blue-600 to-indigo-700 hover:from-blue-700 hover:to-indigo-800"
                      onClick={() => {
                        setActivePage('signup');
                        setIsMobileMenuOpen(false);
                      }}
                      whileHover={{ x: 4 }}
                      whileTap={{ scale: 0.98 }}
                    >
                      <UserPlusIcon className="h-5 w-5 mr-3" />
                      <span>Sign Up</span>
                    </motion.button>
                  </>
                )}
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </motion.header>
  );
};

export default Header;
