import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  HomeIcon, 
  ClipboardDocumentListIcon, 
  InformationCircleIcon,
  UserIcon,
  UserPlusIcon,
  ArrowRightOnRectangleIcon,
  Bars3Icon,
  XMarkIcon,
  ChevronDownIcon
} from '@heroicons/react/24/outline';
import { useApp } from '../../context/AppContext';

const Header = () => {
  const { activePage, setActivePage, isAuthenticated, user, logout } = useApp();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isGetStartedOpen, setIsGetStartedOpen] = useState(false);
  const dropdownRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsGetStartedOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const navigationItems = [
    { id: 'dashboard', label: 'Home', icon: HomeIcon },
    { id: 'about', label: 'About', icon: InformationCircleIcon },
    { id: 'tasks', label: 'Projects', icon: ClipboardDocumentListIcon },
    { id: 'contact', label: 'Contact', icon: UserIcon }
  ];

  const authItems = isAuthenticated 
    ? [
        { id: 'profile', label: user?.name || 'Profile', icon: UserIcon },
        { id: 'logout', label: 'Logout', icon: ArrowRightOnRectangleIcon, action: logout }
      ]
    : [
        { id: 'get-started', label: 'Get Started', icon: UserPlusIcon, isGetStarted: true }
      ];

  const handleNavClick = (item) => {
    if (item.action) item.action();
    else if (item.isGetStarted) setIsGetStartedOpen(!isGetStartedOpen);
    else setActivePage(item.id);
    
    setIsMobileMenuOpen(false);
  };

  return (
    <motion.header 
      className="sticky top-0 z-50 bg-white border-b border-gray-200 shadow-sm"
      initial={{ y: -100, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16 items-center">
          {/* Logo */}
          <motion.div 
            className="flex items-center cursor-pointer"
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
            onClick={() => setActivePage('dashboard')}
          >
            <div className="text-xl font-bold">
              <span className="text-gray-800">Project</span>
              <span className="text-red-600">Flow</span>
            </div>
          </motion.div>

          {/* Desktop Navigation */}
          <div className="hidden md:flex items-center space-x-1">
            {navigationItems.map((item) => (
              <NavItem 
                key={item.id}
                item={item}
                isActive={activePage === item.id}
                onClick={() => handleNavClick(item)}
              />
            ))}
          </div>

          {/* Desktop Auth Section */}
          <div className="hidden md:flex items-center space-x-2">
            {authItems.map((item, index) => (
              <AuthItem 
                key={item.id}
                item={item}
                index={index}
                isGetStartedOpen={isGetStartedOpen}
                dropdownRef={dropdownRef}
                handleNavClick={handleNavClick}
              />
            ))}
          </div>

          {/* Mobile Menu Button */}
          <div className="md:hidden">
            <motion.button
              className="p-2 rounded-md text-gray-700 hover:bg-gray-100"
              onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
              whileTap={{ scale: 0.95 }}
            >
              {isMobileMenuOpen ? (
                <XMarkIcon className="h-6 w-6" />
              ) : (
                <Bars3Icon className="h-6 w-6" />
              )}
            </motion.button>
          </div>
        </div>
      </div>

      {/* Mobile Menu */}
      <AnimatePresence>
        {isMobileMenuOpen && (
          <motion.div
            className="md:hidden"
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: 'auto' }}
            exit={{ opacity: 0, height: 0 }}
            transition={{ duration: 0.3 }}
          >
            <div className="px-2 pt-2 pb-3 space-y-1 sm:px-3 bg-white border-t border-gray-200">
              {navigationItems.map((item) => (
                <MobileNavItem 
                  key={item.id}
                  item={item}
                  isActive={activePage === item.id}
                  onClick={() => handleNavClick(item)}
                />
              ))}
              
              <div className="pt-4 border-t border-gray-200">
                {isAuthenticated ? (
                  authItems.map((item) => (
                    <MobileAuthItem 
                      key={item.id}
                      item={item}
                      onClick={() => handleNavClick(item)}
                    />
                  ))
                ) : (
                  <>
                    <MobileAuthItem 
                      item={{ id: 'login', label: 'Login', icon: UserIcon }}
                      onClick={() => setActivePage('login')}
                    />
                    <MobileAuthItem 
                      item={{ 
                        id: 'signup', 
                        label: 'Sign Up', 
                        icon: UserPlusIcon,
                        isSignup: true 
                      }}
                      onClick={() => setActivePage('signup')}
                    />
                  </>
                )}
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </motion.header>
  );
};

// Reusable Nav Item Component
const NavItem = ({ item, isActive, onClick }) => {
  const Icon = item.icon;
  return (
    <motion.button
      className={`flex items-center px-4 py-2 rounded-md text-sm font-medium transition-colors ${
        isActive 
          ? 'text-red-600 bg-red-50 border-b-2 border-red-600' 
          : 'text-gray-700 hover:bg-gray-100'
      }`}
      onClick={onClick}
      whileHover={{ y: -2 }}
      whileTap={{ scale: 0.98 }}
    >
      <Icon className="h-5 w-5 mr-2" />
      {item.label}
    </motion.button>
  );
};

// Reusable Auth Item Component
const AuthItem = ({ item, index, isGetStartedOpen, dropdownRef, handleNavClick }) => {
  const Icon = item.icon;
  
  if (item.isGetStarted) {
    return (
      <div className="relative" ref={dropdownRef}>
        <motion.button
          className="flex items-center px-4 py-2 rounded-md text-sm font-medium text-white bg-red-600 hover:bg-red-700 transition-colors"
          onClick={() => handleNavClick(item)}
          whileHover={{ y: -2 }}
          whileTap={{ scale: 0.96 }}
          initial={{ opacity: 0, x: 20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ delay: index * 0.1 }}
        >
          <Icon className="h-4 w-4 mr-1" />
          <span>{item.label}</span>
          <ChevronDownIcon className={`h-4 w-4 ml-1 transition-transform ${isGetStartedOpen ? 'rotate-180' : ''}`} />
        </motion.button>
        
        <AnimatePresence>
          {isGetStartedOpen && (
            <motion.div
              className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg py-1 border border-gray-200 z-10"
              initial={{ opacity: 0, y: -10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.2 }}
            >
              <DropdownItem 
                icon={UserIcon}
                label="Login"
                onClick={() => {
                  setActivePage('login');
                  setIsGetStartedOpen(false);
                }}
              />
              <DropdownItem 
                icon={UserPlusIcon}
                label="Sign Up"
                onClick={() => {
                  setActivePage('signup');
                  setIsGetStartedOpen(false);
                }}
              />
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    );
  }
  
  return (
    <motion.button
      className={`flex items-center px-4 py-2 rounded-md text-sm font-medium transition-colors ${
        item.id === 'logout' 
          ? 'text-gray-700 hover:text-red-600 hover:bg-red-50 border border-gray-300' 
          : 'text-gray-700 hover:bg-gray-100'
      }`}
      onClick={() => handleNavClick(item)}
      whileHover={{ y: -2 }}
      whileTap={{ scale: 0.96 }}
      initial={{ opacity: 0, x: 20 }}
      animate={{ opacity: 1, x: 0 }}
      transition={{ delay: index * 0.1 }}
    >
      <Icon className="h-4 w-4 mr-2" />
      <span>{item.label}</span>
    </motion.button>
  );
};

// Reusable Dropdown Item
const DropdownItem = ({ icon: Icon, label, onClick }) => (
  <motion.button
    className="flex items-center w-full px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
    onClick={onClick}
    whileHover={{ x: 4 }}
  >
    <Icon className="h-4 w-4 mr-3" />
    {label}
  </motion.button>
);

// Mobile Navigation Item
const MobileNavItem = ({ item, isActive, onClick }) => {
  const Icon = item.icon;
  return (
    <motion.button
      className={`flex items-center w-full px-3 py-2 rounded-md text-base font-medium ${
        isActive 
          ? 'text-red-600 bg-red-50' 
          : 'text-gray-700 hover:bg-gray-100'
      }`}
      onClick={onClick}
      whileHover={{ x: 4 }}
      whileTap={{ scale: 0.98 }}
    >
      <Icon className="h-5 w-5 mr-3" />
      {item.label}
    </motion.button>
  );
};

// Mobile Auth Item
const MobileAuthItem = ({ item, onClick }) => {
  const Icon = item.icon;
  return (
    <motion.button
      className={`flex items-center w-full px-3 py-2 rounded-md text-base font-medium ${
        item.isSignup 
          ? 'text-white bg-red-600 hover:bg-red-700' 
          : 'text-gray-700 hover:bg-gray-100'
      }`}
      onClick={onClick}
      whileHover={{ x: 4 }}
      whileTap={{ scale: 0.98 }}
    >
      <Icon className="h-5 w-5 mr-3" />
      {item.label}
    </motion.button>
  );
};

export default Header;
