import React, { useState } from 'react';
import { motion } from 'framer-motion';
import {
  EnvelopeIcon,
  PhoneIcon,
  MapPinIcon,
  ClockIcon,
  ChatBubbleLeftRightIcon,
  UserIcon,
  BuildingOfficeIcon,
  CheckIcon
} from '@heroicons/react/24/outline';

const Contact = ({ setActivePage }) => { // Add setActivePage prop
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    company: '',
    subject: '',
    message: '',
    inquiryType: 'general'
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        duration: 0.6,
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: { duration: 0.5 }
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    // Simulate form submission
    await new Promise(resolve => setTimeout(resolve, 2000));
    
    setIsSubmitting(false);
    setIsSubmitted(true);
    
    // Reset form after 3 seconds
    setTimeout(() => {
      setIsSubmitted(false);
      setFormData({
        name: '',
        email: '',
        company: '',
        subject: '',
        message: '',
        inquiryType: 'general'
      });
    }, 3000);
  };

  const contactInfo = [
    {
      icon: EnvelopeIcon,
      title: 'Email Us',
      content: 'support@projectmanager.com',
      description: 'Send us an email anytime!',
      color: 'blue'
    },
    {
      icon: PhoneIcon,
      title: 'Call Us',
      content: '+1 (555) 123-4567',
      description: 'Mon-Fri from 8am to 5pm',
      color: 'green'
    },
    {
      icon: MapPinIcon,
      title: 'Visit Us',
      content: '123 Business Ave, Suite 100',
      description: 'San Francisco, CA 94105',
      color: 'purple'
    },
    {
      icon: ClockIcon,
      title: 'Business Hours',
      content: 'Monday - Friday',
      description: '8:00 AM - 5:00 PM PST',
      color: 'orange'
    }
  ];

  const inquiryTypes = [
    { value: 'general', label: 'General Inquiry' },
    { value: 'support', label: 'Technical Support' },
    { value: 'sales', label: 'Sales' },
    { value: 'partnership', label: 'Partnership' },
    { value: 'feedback', label: 'Feedback' }
  ];

  return (
    <motion.div
      className="contact-page"
      variants={containerVariants}
      initial="hidden"
      animate="visible"
      aria-label="Contact Page"
    >
      {/* Hero Section */}
      <motion.section className="contact-hero" variants={itemVariants}>
        <div className="hero-content">
          <h1>Get in Touch</h1>
          <p>We'd love to hear from you. Send us a message and we'll respond as soon as possible.</p>
        </div>
      </motion.section>

      {/* Contact Info Cards */}
      <motion.section className="contact-info-section" variants={itemVariants}>
        <div className="info-grid">
          {contactInfo.map((info, index) => {
            const Icon = info.icon;
            return (
              <motion.div
                key={info.title}
                className={`info-card info-${info.color}`}
                variants={itemVariants}
                whileHover={{ scale: 1.02, y: -4 }}
                whileTap={{ scale: 0.98 }}
              >
                <div className="info-icon">
                  <Icon className="w-6 h-6" />
                </div>
                <div className="info-content">
                  <h3>{info.title}</h3>
                  <p className="info-main">{info.content}</p>
                  <p className="info-description">{info.description}</p>
                </div>
              </motion.div>
            );
          })}
        </div>
      </motion.section>

      {/* Main Content */}
      <div className="contact-main">
        {/* Contact Form */}
        <motion.section className="contact-form-section" variants={itemVariants}>
          <div className="form-header">
            <h2>Send us a Message</h2>
            <p>Fill out the form below and we'll get back to you within 24 hours.</p>
          </div>

          {isSubmitted ? (
            <motion.div
              className="success-message"
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              transition={{ type: "spring", duration: 0.5 }}
            >
              <CheckIcon className="w-12 h-12" />
              <h3>Message Sent Successfully!</h3>
              <p>Thank you for contacting us. We'll get back to you soon.</p>
            </motion.div>
          ) : (
            <form onSubmit={handleSubmit} className="contact-form">
              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="name">
                    <UserIcon className="w-5 h-5" />
                    Full Name *
                  </label>
                  <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                    placeholder="Enter your full name"
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="email">
                    <EnvelopeIcon className="w-5 h-5" />
                    Email Address *
                  </label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    required
                    placeholder="Enter your email"
                  />
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="company">
                    <BuildingOfficeIcon className="w-5 h-5" />
                    Company (Optional)
                  </label>
                  <input
                    type="text"
                    id="company"
                    name="company"
                    value={formData.company}
                    onChange={handleInputChange}
                    placeholder="Enter your company name"
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="inquiryType">
                    <ChatBubbleLeftRightIcon className="w-5 h-5" />
                    Inquiry Type
                  </label>
                  <select
                    id="inquiryType"
                    name="inquiryType"
                    value={formData.inquiryType}
                    onChange={handleInputChange}
                  >
                    {inquiryTypes.map(type => (
                      <option key={type.value} value={type.value}>
                        {type.label}
                      </option>
                    ))}
                  </select>
                </div>
              </div>

              <div className="form-group">
                <label htmlFor="subject">Subject *</label>
                <input
                  type="text"
                  id="subject"
                  name="subject"
                  value={formData.subject}
                  onChange={handleInputChange}
                  required
                  placeholder="What's this about?"
                />
              </div>

              <div className="form-group">
                <label htmlFor="message">Message *</label>
                <textarea
                  id="message"
                  name="message"
                  value={formData.message}
                  onChange={handleInputChange}
                  required
                  rows={6}
                  placeholder="Tell us more about your inquiry..."
                />
              </div>

              <motion.button
                type="submit"
                className="submit-btn"
                disabled={isSubmitting}
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
              >
                {isSubmitting ? 'Sending...' : 'Send Message'}
              </motion.button>
            </form>
          )}
        </motion.section>

        {/* FAQ Section */}
        <motion.section className="faq-section" variants={itemVariants}>
          <h2>Frequently Asked Questions</h2>
          
          <div className="faq-list">
            <div className="faq-item">
              <h3>How quickly will I receive a response?</h3>
              <p>We typically respond to all inquiries within 24 hours during business days.</p>
            </div>
            
            <div className="faq-item">
              <h3>What support channels do you offer?</h3>
              <p>We offer email support, live chat, and phone support during business hours.</p>
            </div>
            
            <div className="faq-item">
              <h3>Do you offer custom solutions?</h3>
              <p>Yes! We work with businesses of all sizes to create custom project management solutions.</p>
            </div>
            
            <div className="faq-item">
              <h3>Is there a free trial available?</h3>
              <p>Yes, we offer a 14-day free trial with full access to all features.</p>
            </div>
          </div>
        </motion.section>
      </div>

      <style jsx>{`
        :root {
          --primary-color: #3b82f6;
          --accent-color: #10b981;
          --warning-color: #f59e0b;
          --error-color: #ef4444;
          --text-primary: #111827;
          --text-secondary: #6b7280;
          --background-light: #fff;
          --background-gray: #f9fafb;
          --border-color: #e5e7eb;
          --radius-md: 0.5rem;
          --radius-lg: 1rem;
          --shadow-sm: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
          --shadow-md: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
          --shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
        }

        .contact-page {
          min-height: 100vh;
          background: var(--background-gray);
        }

        /* Hero Section */
        .contact-hero {
          background: linear-gradient(135deg, var(--primary-color) 0%, #1d4ed8 100%);
          color: white;
          padding: 4rem 1rem;
          text-align: center;
        }

        .hero-content {
          max-width: 800px;
          margin: 0 auto;
        }

        .hero-content h1 {
          font-size: 3rem;
          font-weight: 700;
          margin-bottom: 1rem;
          line-height: 1.2;
        }

        .hero-content p {
          font-size: 1.2rem;
          opacity: 0.9;
          max-width: 600px;
          margin: 0 auto;
          line-height: 1.6;
        }

        /* Contact Info Section */
        .contact-info-section {
          padding: 4rem 1rem;
          max-width: 1200px;
          margin: 0 auto;
        }

        .info-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
          gap: 2rem;
        }

        .info-card {
          background: var(--background-light);
          padding: 2rem;
          border-radius: var(--radius-lg);
          box-shadow: var(--shadow-sm);
          border: 1px solid var(--border-color);
          display: flex;
          align-items: flex-start;
          gap: 1rem;
          transition: all 0.3s ease;
        }

        .info-icon {
          width: 3rem;
          height: 3rem;
          border-radius: var(--radius-md);
          display: flex;
          align-items: center;
          justify-content: center;
          color: white;
          flex-shrink: 0;
        }

        .info-blue .info-icon { background: linear-gradient(135deg, #3b82f6, #1d4ed8); }
        .info-green .info-icon { background: linear-gradient(135deg, #10b981, #047857); }
        .info-purple .info-icon { background: linear-gradient(135deg, #8b5cf6, #7c3aed); }
        .info-orange .info-icon { background: linear-gradient(135deg, #f59e0b, #d97706); }

        .info-content h3 {
          font-size: 1.25rem;
          font-weight: 600;
          color: var(--text-primary);
          margin-bottom: 0.5rem;
        }

        .info-main {
          font-weight: 600;
          color: var(--text-primary);
          margin-bottom: 0.25rem;
        }

        .info-description {
          color: var(--text-secondary);
          font-size: 0.9rem;
        }

        /* Main Content */
        .contact-main {
          max-width: 1200px;
          margin: 0 auto;
          padding: 0 1rem 4rem;
          display: grid;
          grid-template-columns: 2fr 1fr;
          gap: 4rem;
        }

        /* Contact Form */
        .contact-form-section {
          background: var(--background-light);
          padding: 3rem;
          border-radius: var(--radius-lg);
          box-shadow: var(--shadow-md);
          border: 1px solid var(--border-color);
        }

        .form-header {
          margin-bottom: 2rem;
        }

        .form-header h2 {
          font-size: 2rem;
          font-weight: 700;
          color: var(--text-primary);
          margin-bottom: 0.5rem;
        }

        .form-header p {
          color: var(--text-secondary);
          font-size: 1.1rem;
        }

        .contact-form {
          display: flex;
          flex-direction: column;
          gap: 1.5rem;
        }

        .form-row {
          display: grid;
          grid-template-columns: 1fr 1fr;
          gap: 1.5rem;
        }

        .form-group {
          display: flex;
          flex-direction: column;
          gap: 0.5rem;
        }

        .form-group label {
          font-weight: 600;
          color: var(--text-primary);
          display: flex;
          align-items: center;
          gap: 0.5rem;
          font-size: 0.9rem;
        }

        .form-group input,
        .form-group select,
        .form-group textarea {
          padding: 0.75rem 1rem;
          border: 1px solid var(--border-color);
          border-radius: var(--radius-md);
          font-size: 1rem;
          transition: all 0.2s ease;
          background: var(--background-light);
        }

        .form-group input:focus,
        .form-group select:focus,
        .form-group textarea:focus {
          outline: none;
          border-color: var(--primary-color);
          box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
        }

        .form-group textarea {
          resize: vertical;
          min-height: 120px;
        }

        .submit-btn {
          background: var(--primary-color);
          color: white;
          border: none;
          padding: 1rem 2rem;
          border-radius: var(--radius-md);
          font-size: 1.1rem;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.2s ease;
          align-self: flex-start;
        }

        .submit-btn:hover:not(:disabled) {
          background: #1d4ed8;
          transform: translateY(-1px);
        }

        .submit-btn:disabled {
          opacity: 0.7;
          cursor: not-allowed;
        }

        /* Success Message */
        .success-message {
          text-align: center;
          padding: 3rem;
          color: var(--accent-color);
        }

        .success-message svg {
          margin-bottom: 1rem;
        }

        .success-message h3 {
          font-size: 1.5rem;
          font-weight: 600;
          margin-bottom: 0.5rem;
          color: var(--text-primary);
        }

        .success-message p {
          color: var(--text-secondary);
        }

        /* FAQ Section */
        .faq-section {
          background: var(--background-light);
          padding: 3rem;
          border-radius: var(--radius-lg);
          box-shadow: var(--shadow-md);
          border: 1px solid var(--border-color);
          height: fit-content;
        }

        .faq-section h2 {
          font-size: 1.5rem;
          font-weight: 700;
          color: var(--text-primary);
          margin-bottom: 2rem;
        }

        .faq-list {
          display: flex;
          flex-direction: column;
          gap: 1.5rem;
        }

        .faq-item {
          padding-bottom: 1.5rem;
          border-bottom: 1px solid var(--border-color);
        }

        .faq-item:last-child {
          border-bottom: none;
          padding-bottom: 0;
        }

        .faq-item h3 {
          font-size: 1.1rem;
          font-weight: 600;
          color: var(--text-primary);
          margin-bottom: 0.5rem;
        }

        .faq-item p {
          color: var(--text-secondary);
          line-height: 1.6;
        }

        /* Responsive Design */
        @media (max-width: 1024px) {
          .contact-main {
            grid-template-columns: 1fr;
            gap: 2rem;
          }

          .contact-form-section,
          .faq-section {
            padding: 2rem;
          }
        }

        @media (max-width: 768px) {
          .hero-content h1 {
            font-size: 2.5rem;
          }

          .hero-content p {
            font-size: 1.1rem;
          }

          .contact-info-section {
            padding: 2rem 1rem;
          }

          .info-grid {
            grid-template-columns: 1fr;
          }

          .form-row {
            grid-template-columns: 1fr;
          }

          .contact-form-section,
          .faq-section {
            padding: 1.5rem;
          }
        }

        @media (max-width: 480px) {
