import React, { useMemo } from 'react';
import { motion } from 'framer-motion';
import {
  CheckCircleIcon,
  ClockIcon,
  ExclamationTriangleIcon,
  PlusIcon,
  ArrowTrendingUpIcon,
  ChartBarIcon
} from '@heroicons/react/24/outline';
import { useApp } from '../context/AppContext';

const Dashboard = () => {
  const { tasks = [], setActivePage } = useApp();

  // Memoize derived data for performance and clarity
  const today = useMemo(() => new Date(), []);
  const stats = useMemo(() => {
    const total = tasks.length;
    const completed = tasks.filter(t => t.isDone).length;
    const pending = total - completed;
    const highPriority = tasks.filter(t => t.priority === 'High' && !t.isDone).length;
    const completionRate = total ? Math.round((completed / total) * 100) : 0;
    return [
      {
        title: 'Total Tasks',
        value: total,
        icon: ChartBarIcon,
        color: 'blue',
        trend: '+12%'
      },
      {
        title: 'Completed',
        value: completed,
        icon: CheckCircleIcon,
        color: 'green',
        trend: `${completionRate}%`
      },
      {
        title: 'Pending',
        value: pending,
        icon: ClockIcon,
        color: 'yellow',
        trend: '-5%'
      },
      {
        title: 'High Priority',
        value: highPriority,
        icon: ExclamationTriangleIcon,
        color: 'red',
        trend: highPriority ? `${highPriority} urgent` : 'Good'
      }
    ];
  }, [tasks]);

  const quickActions = [
    {
      title: 'Add New Task',
      description: 'Create a new task quickly',
      icon: PlusIcon,
      action: () => setActivePage('tasks'),
      color: 'blue'
    },
    {
      title: 'View All Tasks',
      description: 'Manage your task list',
      icon: ChartBarIcon,
      action: () => setActivePage('tasks'),
      color: 'green'
    },
    {
      title: 'Analytics',
      description: 'View performance metrics',
      icon: ArrowTrendingUpIcon,
      action: () => setActivePage('analytics'),
      color: 'purple'
    }
  ];

  const overdueTasks = useMemo(
    () => tasks.filter(t => t.dueDate && !t.isDone && new Date(t.dueDate) < today).length,
    [tasks, today]
  );
  const recentTasks = useMemo(
    () =>
      tasks
        .slice()
        .sort((a, b) => new Date(b.createdAt || 0) - new Date(a.createdAt || 0))
        .slice(0, 5),
    [tasks]
  );

  // Animation variants
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { opacity: 1, transition: { staggerChildren: 0.1 } }
  };
  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: { y: 0, opacity: 1, transition: { duration: 0.3 } }
  };

  return (
    <motion.div
      className="dashboard"
      variants={containerVariants}
      initial="hidden"
      animate="visible"
      aria-label="Dashboard"
    >
      {/* Welcome Header */}
      <motion.section className="welcome-section" variants={itemVariants} aria-label="Welcome">
        <div className="welcome-content">
          <h1 tabIndex={0}>Welcome back! 👋</h1>
          <p tabIndex={0}>Here's what's happening with your projects today.</p>
        </div>
        <motion.button
          className="btn btn-primary"
          onClick={() => setActivePage('tasks')}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          aria-label="Add new task"
        >
          <PlusIcon className="w-5 h-5" />
          Add Task
        </motion.button>
      </motion.section>

      {/* Stats Overview */}
      <motion.div className="stats-grid" variants={itemVariants} aria-label="Statistics">
        {stats.map(stat => {
          const Icon = stat.icon;
          return (
            <motion.div
              key={stat.title}
              className={`stat-card stat-${stat.color}`}
              variants={itemVariants}
              whileHover={{ scale: 1.02, y: -2 }}
              aria-label={`Stat: ${stat.title}`}
              tabIndex={0}
            >
              <div className="stat-icon">
                <Icon className="w-6 h-6" aria-hidden="true" />
              </div>
              <div className="stat-content">
                <h3>{stat.value}</h3>
                <p>{stat.title}</p>
                <span className="stat-trend">{stat.trend}</span>
              </div>
            </motion.div>
          );
        })}
      </motion.div>

      {/* Main Grid */}
      <div className="main-grid">
        {/* Quick Actions Section */}
        <motion.section className="quick-actions" variants={itemVariants} aria-label="Quick Actions">
          <h2>Quick Actions</h2>
          <div className="actions-grid">
            {quickActions.map(action => {
              const Icon = action.icon;
              return (
                <motion.button
                  key={action.title}
                  className={`action-card action-${action.color}`}
                  onClick={action.action}
                  variants={itemVariants}
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  aria-label={action.title}
                >
                  <div className="action-icon">
                    <Icon className="w-6 h-6" aria-hidden="true" />
                  </div>
                  <div className="action-content">
                    <h3>{action.title}</h3>
                    <p>{action.description}</p>
                  </div>
                </motion.button>
              );
            })}
          </div>
        </motion.section>

        {/* Recent Tasks Section */}
        <motion.section className="recent-tasks" variants={itemVariants} aria-label="Recent Tasks">
          <div className="section-header">
            <h2>Recent Tasks</h2>
            <button className="btn btn-secondary btn-sm" onClick={() => setActivePage('tasks')}>
              View All
            </button>
          </div>
          <div className="tasks-list">
            {recentTasks.length > 0 ? (
              recentTasks.map(task => (
                <motion.div
                  key={task._id}
                  className="task-item"
                  variants={itemVariants}
                  whileHover={{ x: 4 }}
                  aria-label={`Task: ${task.taskName}`}
                  tabIndex={0}
                >
                  <div
                    className={`status-indicator ${task.isDone ? 'completed' : 'pending'}`}
                    aria-label={task.isDone ? 'Completed' : 'Pending'}
                  />
                  <div className="task-content">
                    <h4 className={task.isDone ? 'completed' : ''}>{task.taskName}</h4>
                    <div className="task-meta">
                      <span className={`priority priority-${task.priority.toLowerCase()}`}>
                        {task.priority}
                      </span>
                      {task.dueDate && (
                        <span className="due-date">
                          Due: {new Date(task.dueDate).toLocaleDateString()}
                        </span>
                      )}
                    </div>
                  </div>
                </motion.div>
              ))
            ) : (
              <div className="empty-state">
                <p>No tasks yet. Start by creating your first task!</p>
                <button className="btn btn-primary" onClick={() => setActivePage('tasks')}>
                  Create Task
                </button>
              </div>
            )}
          </div>
        </motion.section>
      </div>

      {/* Overdue Alert */}
      {overdueTasks > 0 && (
        <motion.div
          className="alert alert-warning"
          variants={itemVariants}
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          role="alert"
          tabIndex={0}
        >
          <ExclamationTriangleIcon className="w-5 h-5" />
          <span>
            You have {overdueTasks} overdue task{overdueTasks > 1 ? 's' : ''}!
          </span>
          <button className="btn btn-sm" onClick={() => setActivePage('tasks')}>
            View Tasks
          </button>
        </motion.div>
      )}

      {/* Styles (unchanged except grouped/commented for clarity) */}
      <style jsx>{`
        /* ...group your CSS and use comments for sections as in your original... */
      `}</style>
    </motion.div>
  );
};

export default Dashboard;
