import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  HomeIcon, 
  ClipboardDocumentListIcon, 
  InformationCircleIcon,
  UserIcon,
  UserPlusIcon,
  ArrowRightOnRectangleIcon,
  Bars3Icon,
  XMarkIcon
} from '@heroicons/react/24/outline';
import { useApp } from '../../context/AppContext';

const Header = () => {
  const { activePage, setActivePage, isAuthenticated, user, logout } = useApp();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const navigationItems = [
    { id: 'dashboard', label: 'Dashboard', icon: HomeIcon },
    { id: 'tasks', label: 'Tasks', icon: ClipboardDocumentListIcon },
    { id: 'about', label: 'About', icon: InformationCircleIcon }
  ];

  const authItems = isAuthenticated 
    ? [
        { id: 'profile', label: user?.name || 'Profile', icon: UserIcon },
        { id: 'logout', label: 'Logout', icon: ArrowRightOnRectangleIcon, action: logout }
      ]
    : [
        { id: 'login', label: 'Login', icon: UserIcon },
        { id: 'signup', label: 'Sign Up', icon: UserPlusIcon }
      ];

  const handleNavClick = (item) => {
    if (item.action) {
      item.action();
    } else {
      setActivePage(item.id);
    }
    setIsMobileMenuOpen(false); // Close mobile menu when navigating
  };

  return (
    <motion.header 
      className="header"
      initial={{ y: -100, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <nav className="navbar">
        <div className="container">
          <div className="navbar-content">
            {/* Logo/Brand */}
            <motion.div 
              className="navbar-brand"
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              onClick={() => setActivePage('dashboard')}
            >
              <div className="brand-icon">
                <ClipboardDocumentListIcon className="w-7 h-7" />
              </div>
              <div className="brand-text">
                <h1>ProjectFlow</h1>
                <span>Task Management</span>
              </div>
            </motion.div>

            {/* Navigation Links */}
            <div className="navbar-center">
              <div className="navbar-nav">
                {navigationItems.map((item) => {
                  const Icon = item.icon;
                  return (
                    <motion.button
                      key={item.id}
                      className={`nav-link ${activePage === item.id ? 'active' : ''}`}
                      onClick={() => handleNavClick(item)}
                      whileHover={{ y: -2 }}
                      whileTap={{ scale: 0.98 }}
                    >
                      <Icon className="w-5 h-5" />
                      <span>{item.label}</span>
                    </motion.button>
                  );
                })}
              </div>
            </div>

            {/* Auth Section */}
            <div className="navbar-auth">
              {authItems.map((item, index) => {
                const Icon = item.icon;
                const isLogout = item.id === 'logout';
                const isSignup = item.id === 'signup';
                return (
                  <motion.button
                    key={item.id}
                    className={`auth-link ${activePage === item.id ? 'active' : ''} ${isSignup ? 'signup-btn' : ''} ${isLogout ? 'logout-btn' : ''}`}
                    onClick={() => handleNavClick(item)}
                    whileHover={{ y: -2 }}
                    whileTap={{ scale: 0.96 }}
                    initial={{ opacity: 0, x: 20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ delay: index * 0.1 }}
                  >
                    <Icon className="w-4 h-4" />
                    <span>{item.label}</span>
                  </motion.button>
                );
              })}
            </div>
          </div>
        </div>
      </nav>

